import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import './Home.css';
import {PostData} from '../../services/PostData';
import UserFeed from "../UserFeed/UserFeed";
import { confirmAlert } from 'react-confirm-alert';
import '../../styles/react-confirm-alert.css';

class Home extends Component {

constructor(props) {
super(props);

this.state = {
data:[],
redirectToReferrer: false,
p1:'iPhoneX',
p2:'iPad',
name:'',
};

this.getUserFeed = this.getUserFeed.bind(this);
this.addp1 = this.addp1.bind(this);
this.addp1 = this.feedUpdate2.bind(this);
this.onChange = this.onChange.bind(this);
this.deleteFeed = this.deleteFeed.bind(this);
this.deleteFeedAction = this.deleteFeedAction.bind(this);
this.logout = this.logout.bind(this);
}

componentWillMount() {

if(sessionStorage.getItem("userData")){
this.getUserFeed();
}

else{
this.setState({redirectToReferrer: true});
}

}
//-------------------------------------------------------
addp1(e) {

e.preventDefault();
let data = JSON.parse(sessionStorage.getItem("userData"));
let postData = { user_id: data.userData.user_id, feed: this.state.p1 };
if (this.state.p1) {
PostData('feedUpdate', postData).then((result) => {
let responseJson = result;
this.setState({data: responseJson.feedData});
});
}
}
addp2(e) {

    e.preventDefault();
    let data = JSON.parse(sessionStorage.getItem("userData"));
    let postData = { user_id: data.userData.user_id, feed: this.state.userp2 };
    if (this.state.p2) {
    PostData('feedUpdate', postData).then((result) => {
    let responseJson = result;
    this.setState({data: responseJson.feedData});
    });
    }
    }
//---------------------------------------------------------
deleteFeed(e){

confirmAlert({
title: 'Delete Feed',
message: 'Are you sure to delete this feed.',
buttons: [
{
label: 'Yes',
onClick: () => this.deleteFeedAction(e)
},
{
label: 'No',
onClick: () => alert('Click No')
}
]
});

}

deleteFeedAction(e){

let updateIndex=e.target.getAttribute('value');

let feed_id=document.getElementById("del").getAttribute("data");

let data = JSON.parse(sessionStorage.getItem("userData"));

let postData = { user_id: data.userData.user_id,feed_id: feed_id };
if (postData) {

PostData('feedDelete', postData).then((result) => {
this.state.data.splice(updateIndex,1);
this.setState({data:this.state.data});

if(result.success){

alert(result.success);
}
else
alert(result.error);

});
}
}

editFeed(e){

alert("j");
}

getUserFeed() {

let data = JSON.parse(sessionStorage.getItem("userData"));
this.setState({name:data.userData.name});
let postData = { user_id: data.userData.user_id};

if (data) {
PostData('feed', postData).then((result) => {
let responseJson = result;
if(responseJson.feedData){
this.setState({data: responseJson.feedData});
console.log(this.state);
}
});
}

}

onChange(e){
this.setState({userFeed:e.target.value});
}

logout(){
sessionStorage.setItem("userData",'');
sessionStorage.clear();
this.setState({redirectToReferrer: true});
}

render() {
if (this.state.redirectToReferrer) {
return (<Redirect to={'/login'}/>)
}

return (
<div className="row" id="Body">
<div className="medium-12 columns">
<a href="#" onClick={this.logout} className="logout">Logout</a>

<form onSubmit={this.addp1} method="post">
<input onChange={this.onChange} value={this.state.p1} type="hidden" />
<input name="p1" type="submit" value="iPhoneX" className="button" onClick={this.addp1}/>
</form>

<form onSubmit={this.addp2} method="post">
<input onChange={this.onChange} value={this.state.p2} type="hidden" />
<input name="p2" type="submit" value="iPad" className="button" onClick={this.addp2}/>
</form>

</div>
<UserFeed feedData = {this.state.data} deleteFeed = {this.deleteFeed} name={this.state.name}/>

</div>
);
}
}

export default Home;
