import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import {PostData} from '../../services/PostData';
import './Payment.css';
import chip from "./chip.png";
class Payment extends Component {
  constructor(props) {
  super(props);

  this.state = {
  data:[],
  redirectToReferrer: false,
  chips:'',
  name:'',
  };
  this.getuserChips=this.getuserChips.bind(this);
  this.logout = this.logout.bind(this);
  }

  componentWillMount() {

  if(sessionStorage.getItem("userData")){

  this.getuserChips();
  }

  else{
  this.setState({redirectToReferrer: true});
  }

  }
  //---------------------------Add Product----------------------------
  feedUpdate1(e) {
      e.preventDefault();
      let data = JSON.parse(sessionStorage.getItem("userData"));
      let postData = { user_id: data.userData.user_id, feed: this.state.userFeed1,price: this.state.price1};
      if (this.state.userFeed1) {
      PostData('feedUpdate', postData).then((result) => {
      let responseJson = result;
      this.setState({data: responseJson.feedData});

      });
      }
  }



  //---------------------------------------------------------

  getuserChips() {

      let data = JSON.parse(sessionStorage.getItem("userData"));
      this.setState({name:data.userData.name});
      let postData = { user_id: data.userData.user_id};

      if (data) {
      PostData('chips', postData).then((result) => {
      let responseJson = result;
      this.setState({chips: responseJson.chips});
      });
      }
  }


  logout(){
      sessionStorage.setItem("userData",'');
      sessionStorage.clear();
      this.setState({redirectToReferrer: true});
  }

render(){
return (
      <div className="row" id="Body" >
      <a style={{marginTop:"-35px",color:"white"}} href="#" onClick={this.logout} className="logout">LOGOUT</a>
        <div className="medium-5 columns left con" style={{fontFamily:"Cambria"}} >
        <center><h4 style={{fontFamily:"Cambria"}}>BUY CHIPS</h4></center>


        <label><i style={{marginRight:"10px"}}><img src={chip} width="30px" height="30px" /></i> CHIPS:
        <input style={{width:"100px"}} type="text" name="username" placeholder="Username" onChange={this.onChange}/></label>
        <label>Password</label>
        <input type="password" name="password"  placeholder="Password" onChange={this.onChange}/>
        <input type="submit" style={{color:"#1E2F93"}} className="button" value="Login" onClick={this.login}/>
        <a href="/signup" style={{color:"#1E2F93"}}>Registration</a>
        </div>
      </div>
    );
}
}
export default Payment;
