import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import './Home.css';
import {PostData} from '../../services/PostData';
import UserFeedshop from "../UserFeedshop/UserFeedshop";
import { confirmAlert } from 'react-confirm-alert';
import '../../styles/react-confirm-alert.css';

class Home extends Component {

constructor(props) {
super(props);

this.state = {
data:[],
userFeed: '',
p_name: '',
redirectToReferrer: false,
name:'',
};


this.getUserFeedShop = this.getUserFeedShop.bind(this);
this.feedUpdate = this.feedUpdate.bind(this);
this.onChange = this.onChange.bind(this);
this.deleteFeed = this.deleteFeed.bind(this);
this.deleteFeedAction = this.deleteFeedAction.bind(this);
this.addCart = this.addCart.bind(this);
this.addCartAction = this.addCartAction.bind(this);
this.logout = this.logout.bind(this);
}

componentWillMount() {

if(sessionStorage.getItem("userData")){

this.getUserFeedShop();
}

else{
this.setState({redirectToReferrer: true});
}

}

feedUpdate(e) {

e.preventDefault();
let data = JSON.parse(sessionStorage.getItem("userData"));
let postData = { user_id: data.userData.user_id, feed: this.state.userFeed };
if (this.state.userFeed) {
PostData('feedUpdate', postData).then((result) => {
let responseJson = result;
this.setState({data: responseJson.feedData});
});
}
}
//----------------------------------
addCart(e){

    confirmAlert({
    title: 'Add to Cart',
    message: 'Are you sure to add this book.',
    buttons: [
    {
    label: 'Yes',
    onClick: () => this.addCartAction(e)
    },
    {
    label: 'No',
    onClick: () => alert('Click No')
    }
    ]
    });
    
    }
    
    addCartAction(e){
    
    let updateIndex=e.target.getAttribute('value');
    
    
    
    let data = JSON.parse(sessionStorage.getItem("userData"));
    
    let postData = { user_id: data.userData.user_id,p_name: p_name };
    if (postData) {
    
    PostData('add', postData).then((result) => {
    this.state.data.splice(updateIndex,1);
    this.setState({data:this.state.data});
    
    if(result.success){
    
    alert(result.success);
    }
    else
    alert(result.error);
    
    });
    }
    }
//----------------------------------------------------------
deleteFeed(e){

confirmAlert({
title: 'Delete Feed',
message: 'Are you sure to delete this feed.',
buttons: [
{
label: 'Yes',
onClick: () => this.deleteFeedAction(e)
},
{
label: 'No',
onClick: () => alert('Click No')
}
]
});

}

deleteFeedAction(e){

let updateIndex=e.target.getAttribute('value');

let feed_id=document.getElementById("del").getAttribute("data");

let data = JSON.parse(sessionStorage.getItem("userData"));

let postData = { user_id: data.userData.user_id,feed_id: feed_id };
if (postData) {

PostData('feedDelete', postData).then((result) => {
this.state.data.splice(updateIndex,1);
this.setState({data:this.state.data});

if(result.success){

alert(result.success);
}
else
alert(result.error);

});
}
}

editFeed(e){

alert("j");
}


getUserFeedShop() {

    let data = JSON.parse(sessionStorage.getItem("userData"));
    this.setState({name:data.userData.name});
    let postData = { user_id: data.userData.user_id};
    
    if (data) {
    PostData('feedshop', postData).then((result) => {
    let responseJson = result;
    if(responseJson.feedData){
    this.setState({data: responseJson.feedData});
    console.log(this.state);
    }
    });
    }
    
    }

onChange(e){
this.setState({userFeed:e.target.value});
}

onChange(t){
    this.setState({addProduct:t.target.value});
}
logout(){
sessionStorage.setItem("userData",'');
sessionStorage.clear();
this.setState({redirectToReferrer: true});
}

render() {
if (this.state.redirectToReferrer) {
return (<Redirect to={'/login'}/>)
}

return (
<div className="row" id="Body">
<div className="medium-12 columns">
<a href="#" onClick={this.logout} className="logout">Logout</a>
<form onSubmit={this.feedUpdate} method="post">
<input name="userFeed" onChange={this.onChange} value={this.state.userFeed} type="text" placeholder="Name"/>

<input
type="submit"
value="Post"
className="button"
onClick={this.feedUpdate}/>
</form>

</div>

<UserFeedshop feedDatashop = {this.state.data} addProduct = {this.addProduct} name={this.state.name}/>

</div>
);
}
}

export default Home;
