import React, {Component} from 'react';
import {Redirect} from 'react-router-dom';
import './Home.css';
import {PostData} from '../../services/PostData';
import { confirmAlert } from 'react-confirm-alert';
import '../../styles/react-confirm-alert.css';
import iphonex from "./iphonex.png";
import ipad from "./ipad.png";
import watch from "./watch.png";

class Home extends Component {

constructor(props) {
super(props);

this.state = {
data:[],
redirectToReferrer: false,
userFeed1:'iPhoneX',
price:20000,
userFeed2:'iPad',
userFeed3:'AppleWatch',
name:'',
};

this.getUserFeed = this.getUserFeed.bind(this);
this.addcart = this.addcart.bind(this);
this.feedUpdate1 = this.feedUpdate1.bind(this);
this.feedUpdate2 = this.feedUpdate2.bind(this);
this.feedUpdate3 = this.feedUpdate3.bind(this);
this.onChange = this.onChange.bind(this);
this.logout = this.logout.bind(this);
}

componentWillMount() {

if(sessionStorage.getItem("userData")){
this.getUserFeed();
}

else{
this.setState({redirectToReferrer: true});
}

}
//---------------------------Add Product----------------------------
addcart(e){
    confirmAlert({
    title: 'Add to Cart',
    message: 'Product have been added to your cart.',
    buttons: [{ label: 'OK'}]});
}
feedUpdate1(e) {
    e.preventDefault();
    let data = JSON.parse(sessionStorage.getItem("userData"));
    let postData = { user_id: data.userData.user_id, feed: this.state.userFeed1,p_price:this.state.price};
    if (this.state.userFeed1) {
    PostData('feedUpdate', postData).then((result) => {
    let responseJson = result;
    this.setState({data: responseJson.feedData});
    
    });
    }
}

feedUpdate2(e) {
    e.preventDefault();
    let data = JSON.parse(sessionStorage.getItem("userData"));
    let postData = { user_id: data.userData.user_id, feed: this.state.userFeed2 };
    if (this.state.userFeed2) {
    PostData('feedUpdate', postData).then((result) => {
    let responseJson = result;
    this.setState({data: responseJson.feedData});
    });
    }
}
feedUpdate3(e) {
    e.preventDefault();
    let data = JSON.parse(sessionStorage.getItem("userData"));
    let postData = { user_id: data.userData.user_id, feed: this.state.userFeed3};
    if (this.state.userFeed3) {
    PostData('feedUpdate', postData).then((result) => {
    let responseJson = result;
    this.setState({data: responseJson.feedData});
    
    });
    }
}
//---------------------------------------------------------
getUserFeed() {

    let data = JSON.parse(sessionStorage.getItem("userData"));
    this.setState({name:data.userData.name});
    let postData = { user_id: data.userData.user_id};

    if (data) {
    PostData('feed', postData).then((result) => {
    let responseJson = result;
    if(responseJson.feedData){
    this.setState({data: responseJson.feedData});
    
    }
    });
    }
}
onChange(e){
    this.setState({userFeed:e.target.value});
}
logout(){
    sessionStorage.setItem("userData",'');
    sessionStorage.clear();
    this.setState({redirectToReferrer: true});
}

render() {
if (this.state.redirectToReferrer) {
    return (<Redirect to={'/login'}/>)
}

return (
<div className="row" id="Body" >
<div className="medium-12 columns">
<a href="#" onClick={this.logout} className="logout">Logout</a>
<a href="/home" className="button success">Shop</a>
<a href="/cart" className="button success">Shopping Cart</a>
<h2> Welcome {this.state.name}</h2>
<hr/>

<h3>Most Popular</h3>
<hr/>
<br/>
<h4>iPhone X</h4>
<form onSubmit={this.feedUpdate1} method="post">
<input onChange={this.onChange} value={this.state.userFeed1} type="hidden"/>
<input onChange={this.onChange} value={this.state.price1} type="hidden"/>
<img src={iphonex} width="300px" height="150px" />
<input name="userFeed1" type="submit" value="Add to Cart" className="button" onClick={this.addcart}/>
</form>
<hr/>

<h4>iPad Pro</h4>
<form onSubmit={this.feedUpdate2} method="post">
<input onChange={this.onChange} value={this.state.userFeed2} type="hidden" />
<img src={ipad}  width="300px" height="150px"/>
<input name="userFeed2" type="submit" value="Add to Cart" className="button" onClick={this.addcart}/>
</form>
<hr/>
<h4>Apple Watch</h4>
<form onSubmit={this.feedUpdate3} method="post">
<input onChange={this.onChange} value={this.state.userFeed3} type="hidden" />
<img src={watch}  width="300px" height="150px"/>
<input name="userFeed3" type="submit" value="Add to Cart" className="button" onClick={this.addcart}/>
</form>

</div>

</div>
);
}
}

export default Home;
